import React from "react";
import { Link } from "react-router-dom"; 

const markdownFiles = [
  {
    name: "카테고리1", type: "folder",
    posts: [
      { name: "대학공부", type: "file" }, 
      { name: "유니티", type: "file" }
    ]
  },
  {
    name: "카테고리2", type: "folder",
    posts: [
      { name: "자바스크립트", type: "file" }, 
      { name: "리액트", type: "file" }
    ]
  }
];



export default function Post() {

    const [selectedCategory, setSelectedCategory] = useState(null);

    const handleClickCategory = (categoryName) => {
        setSelectedCategory(categoryName);
      };

  return (
    <div>
    {markdownFiles.map((category, index) => (
      category.type === 'folder' && (
        <div key={index}>
          <h3 onClick={() => handleClickCategory(category.name)} style={{ cursor: 'pointer' }}>
            {category.name}
          </h3>
          {selectedCategory === category.name && (
            <div>
              {category.posts.map((post, idx) => (
                <Link key={idx} to={`/blog/${category.name}/${post.name}`}>
                  <h4>{post.name}</h4>
                </Link>
              ))}
            </div>
          )}
        </div>
      )
    ))}
  </div>
  );
};
