// src/post/PageCategory.js
import React from 'react';
import { Link } from 'react-router-dom';

const markdownFiles = [
  {
    name: "CosProC", type: "folder",
    posts: [
      { id: 1, name: "가장많이받은선물-lv1", type: "file" },
      { id: 2, name: "리눅스", type: "file" }
    ]
  },
  {
    name: "리눅스", type: "folder",
    posts: [
      { id: 3, name: "2020년12월12일", type: "file" },
      { id: 4, name: "2022년09월03일", type: "file" }
    ]
  }
];

const Category = ({ setSelectedCategory }) => {
  const handleCategoryClick = (categoryName) => {
    setSelectedCategory(categoryName);
    setTimeout(() => {
      window.location.reload();  // 페이지 새로고침
    }, 100); // 100ms 후에 새로고침
  };

  return (
    <div>
      <div className="Frofile"> 
        <img src="프로필/앵무새.jpg" width="170" height="150" onclick="change(this)" style="border-radius: 80px; border: 1px solid gray;"/>
      </div>
      
      {markdownFiles.map((category, index) => (
        category.type === 'folder' && (
          <div key={index}>
            <h3>
              <Link to={`/blog/${category.name}`} onClick={() => handleCategoryClick(category.name)}>
                {category.name}
              </Link>
            </h3>
          </div>
        )
      ))}
    </div>
  );
};

export default Category;
