import React, { useState } from "react";
import { Link, Route } from "react-router-dom"; 

export default function Post() {
  const markdownFiles = [
    { name: "카테고리1", post: [
      { name: "대학공부", content: "내용1" }, 
      { name: "유니티", content: "내용2" }
    ]},
    { name: "카테고리2", post: [
      { name: "대학공부", content: "내용1" }, 
      { name: "유니티", content: "내용2" }
    ]}
  ];

  return (
    <div>
      <h2>글</h2>
      <div>
        {markdownFiles.map((file, fileIndex) => (
          <div key={fileIndex}>
            <Link to={`/blog/${file.name}`}>
              <h3>{file.name}</h3>
            </Link>
          </div>
        ))}
      </div>

      <Route path="/blog/:카테고리1">
        <Category />
      </Route>
    </div>
  );
}

function Category() {

  const markdownFiles = [
    { name: "카테고리1", post: [
      { name: "대학공부", content: "내용1" }, 
      { name: "유니티", content: "내용2" }
    ]},
    { name: "카테고리2", post: [
      { name: "대학공부", content: "내용1" }, 
      { name: "유니티", content: "내용2" }
    ]}
  ];

  const category = markdownFiles.find(file => file.name === categoryName);

  if (!category) return <div>해당 카테고리를 찾을 수 없습니다.</div>;

  return (
    <div>
      <h3>{categoryName}</h3>
      {category.post.map((post, postIndex) => (
        <div key={postIndex}>
          <h4>{post.name}</h4>
          <p>{post.content}</p>
        </div>
      ))}
    </div>
  );
}
