import './App.css';
import { Route, Routes, useNavigate } from 'react-router-dom';
import React, { useState } from 'react';

import Post from './post/PagePost';
import Read from './post/PageLoad';
import Category from './post/PageCategory';

function App() { 
  const [selectedCategory, setSelectedCategory] = useState(null);
  const navigate = useNavigate();

  const handleCategorySelect = (categoryName) => {
    setSelectedCategory(categoryName);
    navigate(`/blog/${categoryName}`);
  };

  return (
    <div className="App">
      <Routes> 
        <Route path="/" element={<Home handleCategorySelect={handleCategorySelect} />}/>
        <Route path="/blog" element={<Post selectedCategory={selectedCategory}/>}/> 
        <Route path="/blog/:folderName/:fileName" element={<Read/>}/>
        <Route path="/blog/:categoryName" element={<Post selectedCategory={selectedCategory} />} />
      </Routes>
      <div id="Box2">
        <Category onSelectCategory={handleCategorySelect} />
      </div>
    </div> 
  ); 
}

function Home({ handleCategorySelect }) {
  return (
    <div>
      <br></br>
      <br></br>
      <h1>블로그의 홈 - 나를 소개합니다.</h1>
      <p>블로그 카테고리 글 선택 전의 홈</p>
      <a>게임개발의 끝을 마치긴 해야 함.</a>
      <Category onSelectCategory={handleCategorySelect} />
    </div>
  );
}

export default App;
