import React from "react";
import { Link } from "react-router-dom"; 

export default function Post() {
  const files = [
    { name: "글1", content: "내용1" }, 
    { name: "글2", content: "내용2" }
  ]; //여기 자동으로 카테고리, 이름 찾게 할 거임 

  //카테고리 구현에 필요한 
  const markdownFiles = [
    {name : "카테고리1", post :[
    { name: "대학공부", content: "내용1" }, 
    { name: "유니티", content: "내용2" }
    ]},
    {name : "카테고리", post :[
      { name: "대학공부", content: "내용1" }, 
      { name: "유니티", content: "내용2" }
      ]}
  ]
  //페이지 데이터 받아서 글자 출력  
  if(markdownFiles == "카테고리1" ){
  return (
    <div>
        <h2>글</h2>
    <div>
      {markdownFiles.map((file, index) => (
        <Link
          key={index}
          to={`/blog/${file.name}`}
        >
          <h3>{file.name}</h3>
          <p>{file.content}</p> 
        </Link> 
      ))}
    </div>
    
    </div> 
  );
  }
}
