import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';

const markdownFiles = [
  {
    name: "카테고리1", type: "folder",
    posts: [
      { id: 1, name: "대학공부", type: "file" }, 
      { id: 2, name: "유니티", type: "file" }
    ]
  },
  {
    name: "카테고리2", type: "folder",
    posts: [
      { id: 3, name: "자바스크립트", type: "file" }, 
      { id: 4, name: "리액트", type: "file" }
    ]
  }
];

export default function Post() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const { categoryName } = useParams(); // React Router의 useParams 훅을 사용하여 URL 파라미터 값을 가져옴

  // 카테고리 이름을 클릭했을 때 상태 업데이트
  const handleClickCategory = (categoryName) => {
    setSelectedCategory(categoryName);
  };

  // 선택된 카테고리 객체 찾기
  const selectedCategoryObj = markdownFiles.find(category => category.name === categoryName);

  return (
    <div>
      {markdownFiles.map((category, index) => (
        category.type === 'folder' && (
          <div key={index}>
            <h3>
              <Link to={`/blog/${category.name}`} onClick={() => handleClickCategory(category.name)}>
                {category.name}
              </Link>
            </h3>
          </div>
        )
      ))}
    
    </div>
  );
};

