import React from 'react';
import { Link } from 'react-router-dom';
import { useCategory } from './CategoryContext';

const markdownFiles = [
  { name: "카테고리1", type: "folder", posts: [{ id: 1, name: "대학공부" }, { id: 2, name: "유니티" }] },
  { name: "카테고리2", type: "folder", posts: [{ id: 3, name: "자바스크립트" }, { id: 4, name: "리액트" }] }
];

const Category = () => {
  const { setSelectedCategory } = useCategory();

  const handleCategoryClick = (categoryName) => {
    setSelectedCategory(categoryName);
  };

  return (
    <div>
      {markdownFiles.map((category, index) => (
        category.type === 'folder' && (
          <div key={index}>
            <h3>
              <Link to={`/blog/${category.name}`} onClick={() => handleCategoryClick(category.name)}>
                {category.name}
              </Link>
            </h3>
          </div>
        )
      ))}
    </div>
  );
};

export default Category;
