import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, Link, useRouteMatch, useParams } from "react-router-dom";

export default function Post() {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const { path, url } = useRouteMatch();

  const markdownFiles = [
    {
      name: "대학교 공부정리(B/1-1)",
      posts: [
        { name: "디지털시스템", url: "../대학시절/DigitalSestem.html" },
        { name: "컴퓨터네트워크", url: "../대학시절/Network.html" },
        { name: "리눅스마스터", url: "../대학시절/LinuxMaster.html" },
        { name: "C언어", url: "../대학시절/Cstudy.html" },
        { name: "html + 포토샵", url: "../대학시절/htmljs.html" }
      ]
    }
  ];

  const CategoryList = () => (
    <div>
      {markdownFiles.map((file, index) => (
        <div key={index}>
          <h4>{file.name}</h4>
          <ul>
            {file.posts.map((post, idx) => (
              <li key={idx}>
                <Link to={`${url}/${file.name}/${post.name}`} onClick={() => setSelectedCategory(file)}>
                  {post.name}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );

  const PostDetail = () => {
    const { categoryName, postName } = useParams();
    const category = markdownFiles.find(cat => cat.name === categoryName);
    const post = category.posts.find(p => p.name === postName);

    return (
      <div>
        <h2>{category.name}</h2>
        <h3>{post.name}</h3>
        <p>링크: <a href={post.url} target="_blank" rel="noopener noreferrer">{post.url}</a></p>
      </div>
    );
  };

  return (
    <Router>
      <div className="Box2">
        <h2>카테고리</h2>
        <CategoryList />
        <Switch>
          <Route exact path={path}>
            <h3>카테고리를 선택하세요.</h3>
          </Route>
          <Route path={`${path}/:categoryName/:postName`}>
            <PostDetail />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
