import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';

// 예시 데이터
const markdownFiles = [
  {
    name: "카테고리1", type: "folder",
    posts: [
      { id: 1, name: "대학공부", type: "file" }, 
      { id: 2, name: "유니티", type: "file" }
    ]
  },
  {
    name: "카테고리2", type: "folder",
    posts: [
      { id: 3, name: "자바스크립트", type: "file" }, 
      { id: 4, name: "리액트", type: "file" }
    ]
  }
];

// 포스트를 표시하는 컴포넌트
const Post = ({ categoryName }) => {
  const selectedCategory = markdownFiles.find(category => category.name === categoryName);

  if (!selectedCategory) {
    return null;
  }

  return (
    <div>
      <h2>root: 선택된 카테고리의 포스트 목록</h2>
      <div>
        <h3>{selectedCategory.name}</h3>
        {selectedCategory.posts.map((post) => (
          <div key={post.id}>
            <Link to={`/blog/${selectedCategory.name}/${post.name}`}>
              <h4>{post.name}</h4>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

// 이벤트가 발생한 카테고리 이름을 표시하는 컴포넌트
const Box2 = ({ selectedCategory }) => {
  return (
    <div>
      <h2>Box2: 이벤트가 발생한 카테고리</h2>
      <div>
        <h3>{selectedCategory || '카테고리를 선택하세요'}</h3>
      </div>
    </div>
  );
};

// 최상위 컴포넌트
const BlogApp = () => {
  const { categoryName } = useParams();
  const [selectedCategory, setSelectedCategory] = useState(null);

  // 카테고리 클릭 시 선택된 카테고리 업데이트
  const handleClickCategory = (categoryName) => {
    setSelectedCategory(categoryName);
  };

  return (
    <div>
      {/* Post 컴포넌트에 선택된 카테고리 전달 */}
      <div style={{ float: 'left', width: '50%' }}>
        <Post categoryName={categoryName} />
      </div>
      
      {/* Box2 컴포넌트에 선택된 카테고리 전달 */}
      <div style={{ float: 'left', width: '50%' }}>
        <Box2 selectedCategory={selectedCategory} />
      </div>
    </div>
  );
};

export default BlogApp;
