import React from "react";
import { Link } from "react-router-dom"; 

export default function Post() {

  const [selectedCategory, setSelectedCategory] = useState(null);

  const markdownFiles = [
    {name : "카테고리1", post :[
    { name: "대학공부", content: "내용1" }, 
    { name: "유니티", content: "내용2" }
    ]},
    {name : "카테고리2", post :[
      { name: "대학공부", content: "내용1" }, 
      { name: "유니티", content: "내용2" }
      ]}
  ]

  const handleCategoryClick = (categoryName) => {
    setSelectedCategory(categoryName);
  };

  //페이지 데이터 받아서 글자 출력  
  return (
    <div>
    <h2>글</h2>
    <div>
      {markdownFiles.map((file, fileIndex) => (
        <div key={fileIndex}>
          <h3 onClick={() => handleCategoryClick(file.name)}>{file.name}</h3>
          {selectedCategory === file.name && file.post.map((post, postIndex) => (
            <Link
              key={postIndex}
              to={`/blog/${file.name}/${post.name}`} 
            > 
              <p>{post.name}</p>
              <p>{post.content}</p> 
            </Link> 
          ))}
        </div>
      ))}
    </div>

    </div>

  );
}
